import React from "react";
import "./App.css";
import {
    BrowserRouter as Router,
    Link,
    Route,
    Switch,
    useRouteMatch,
    useParams
} from "react-router-dom";

function App() {
    return (
        <Router>
            <div className="App">
                <h1>Belajar Router</h1>

                <nav>
                    <Link to="/">Home</Link>&nbsp;|&nbsp;
                    <Link to="/about">About</Link>&nbsp;|&nbsp;
                    <Link to="/users">Users</Link>&nbsp;|&nbsp;
                    <Link to="/topics">Topics</Link>
                </nav>

                <Switch>
                    <Route path="/about">
                        <About />
                    </Route>
                    <Route path="/users">
                        <Users />
                    </Route>
                    <Route path="/topics">
                        <Topics />
                    </Route>
                    <Route path="/">
                        <Home />
                    </Route>
                </Switch>
            </div>
        </Router>
    );
}

const Home = () => (
    <div>
        <h2>Home</h2>
    </div>
);

const About = () => (
    <div>
        <h2>About</h2>
    </div>
);

const Users = () => (
    <div>
        <h2>Users</h2>
    </div>
);

const Topics = () => {
    const match = useRouteMatch();
    return (
        <div>
            <Switch>
                <Route path={`${match.url}/:topicid`}>
                    <Topic />
                </Route>
                <Route path={`${match.url}`}>
                    <TopicList />
                </Route>
            </Switch>
        </div>
    );
}

const Topic = () => {
    const { topicid } = useParams();
    return (
        <>
            <h1>Topic: {topicid}</h1>
        </>
    );
}

const TopicList = () => {
    const match = useRouteMatch();
    return (
        <ul style={{ listStyle: "none" }}>
            <li>
                <Link to={`${match.url}/topic-1`}>Topic 1</Link>
            </li>
            <li>
                <Link to={`${match.url}/topic-2`}>Topic 2</Link>
            </li>
            <li>
                <Link to={`${match.url}/topic-3`}>Topic 3</Link>
            </li>
            <li>
                <Link to={`${match.url}/topic-4`}>Topic 4</Link>
            </li>
        </ul>
    );
}

export default App;
